    public static boolean uniqueOccurrences(int[] arr) {
        Arrays.sort(arr);
        int[] occurance1 = new int[arr.length];
        int count = 1;
        int index = 0;
        if(arr.length ==2) return false;
        for(int i=0; i<arr.length-1; i++)
        {
            if(arr[i]==arr[i+1])
            {
                count++;
            }

            if(arr[i] != arr[i+1] || i==arr.length-2)
            {
                occurance1[index] = count;
                count = 1;
                ++index;
            }


        }
        System.out.println(Arrays.toString(occurance1));

        Arrays.sort(occurance1);
        for(int i=occurance1.length-1; i>=0; i--)
        {
            if(occurance1[i]==0)
            {
                break;
            }
            if(occurance1[i] == occurance1[i-1])
            {
                return false;
            }
        }
        return true;
    }

